class RpgForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'investigator3'};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  handleSubmit(event) {
      alert('Você escolheu o personagem: ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Escolha seu personagem para gerar a ficha:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="investigator1">Diana</option>
              <option value="investigator2">John</option>
              <option value="investigator3">Liam</option>
              <option value="villain">Cthulhu Jr.</option>
            </select>
          </label>
          <input type="submit" value="Escolher!"/>
        </form>
      );
    }
    
  }
  
export default RpgForm;
