import React, { useEffect, useState } from "react";
import { encode } from "base-64";

const FormularioPessoa = () => {
  const [formValues, setFormValues] = useState({});

  let headers = new Headers();

  headers.set("Authorization", "Basic " + encode("rhyan" + ":" + "123"));
  
  const onChanged = (e) => {
    e.preventDefault();
    const { value, name } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };
  const onSubmit = (e) => {
    headers.append("Content-Type", "application/json");
    const data = {
      "pessoa": {
        "idPessoa": formValues.pessoa
      },
      "exemplar":{
        "livro": {
          "idLivro": formValues.livro
        } 
      }
    }
    e.preventDefault();
    fetch("http://192.168.1.6:8080/emprestimo/criar", {
      method: "POST",
      headers: headers,
      body: JSON.stringify(data)
    })
      .then((response) => response.json())
      .then(
        (result) => {
          window.alert(result.message);
        },
        (error) => {
          window.alert(error);
        }
      );
  };
  console.log("formValues", formValues);

  const [pessoas, setPessoas] = useState([]);
  const [livros, setLivros] = useState([]);

 
  useEffect(() => {
    headers.append("Content-Type", "text/json");
    fetch("http://192.168.1.6:8080/livro/buscarTodos", {
      method: "GET",
      headers: headers,
    })
      .then((response) => response.json())
      .then(
        (result) => {
          setLivros(result);
        },
        (error) => {
          window.alert(error);
        }
      );
  }, []);
  useEffect(() => {
    headers.append("Content-Type", "text/json");
    fetch("http://192.168.1.6:8080/pessoa/buscarTodas", {
      method: "GET",
      headers: headers,
    })
      .then((response) => response.json())
      .then(
        (result) => {
          setPessoas(result);
        },
        (error) => {
          window.alert(error);
        }
      );
  }, []);

  return (
    <div className="containerForm">
      <div className="paddingContainer">
        <form onSubmit={onSubmit}>
          <h3>Nome</h3>
          <input className="field" type="text" name="name"/>
          <h3>RG</h3>
          <input  type="text" name="name"/>
          <h3>cpf</h3>
          <input type="text" name="name"/>
          <h3>Endereço Comercial</h3>
          <input type="text" name="name"/>
          <h3>Numero</h3>
          <input type="text" name="name"/>
          <h3>Complemento</h3>
          <input type="text" name="name"/>
          <h3>Endereço Residencial</h3>
          <input type="text" name="name"/>
          <h3>Numero</h3>
          <input type="text" name="name"/>
          <h3>Complemento</h3>
          <input type="text" name="name"/>
          


         
          <button >Click me</button>
        </form>
      </div>
    </div>
  );
};

export default FormularioPessoa;
