import React from 'react'
import '../index.css';
import DataTable from "react-data-table-component";

const Dashboard = () => {
        const data = [];
        let headers = new Headers();
        headers.append('Content-Type', 'text/json');
        headers.set('Authorization', 'Basic ' + base64.encode(username + ":" + password));
        fetch("http://192.168.1.6:8080/emprestimo/getAtrasados", { method: 'POST', headers: headers,  
         }).then((response) => response.json())
                .then(
                        (result) => {
                                data = result;
                                debugger;
                        },
                        (error) => {
                        }
                )

        const columns = [
                {
                        name: 'Nome',
                        selector: row => row.title,
                },
                {
                        name: 'Data devolução',
                        selector: row => row.year,
                },
        ];


        return (
                <div className="sec__one">
                        <div className='container_midless'>
                                <div className='title'><h1 className='align_center'>Atrasados</h1></div>
                                <div className='paddingContainer'> </div><DataTable
                                        columns={columns}
                                        data={data}
                                />
                        </div>
                </div>
        )
}
export default Dashboard
