import React, { useEffect, useState } from "react";
import { encode } from "base-64";

const FormularioExemplar = () => {
  const [formValues, setFormValues] = useState({});

  let headers = new Headers();

  headers.set("Authorization", "Basic " + encode("rhyan" + ":" + "123"));

  const onChanged = (e) => {
    e.preventDefault();
    const { value, name } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };
  const onSubmit = (e) => {
    headers.append("Content-Type", "application/json");
    const data = {
      nomeLivro: nome,
      editora: {
        idEditora: formValues.editora,
      },
    };
    e.preventDefault();
    fetch("http://192.168.1.6:8080/exemplar/criar", {
      method: "POST",
      headers: headers,
      body: JSON.stringify(data),
    })
      .then((response) => response)
      .then(
        (result) => {
          if (result.status == 200) onSucess();
        },
        (error) => {
          window.alert(error);
        }
      );
  };
  const onSucess = () => {
    alert("Livro registrada");
    window.location.reload(true);
  };
  console.log("formValues", formValues);
  const [livros, setLivros] = useState([]);

  useEffect(() => {
    headers.append("Content-Type", "text/json");
    fetch("http://192.168.1.6:8080/livro/buscarTodos", {
      method: "GET",
      headers: headers,
    })
      .then((response) => response.json())
      .then(
        (result) => {
            setLivros(result);
        },
        (error) => {
          window.alert(error);
        }
      );
  }, []);

  return (
    <div className="containerForm">
      <div className="paddingContainer">
        <form onSubmit={onSubmit}>
          <h3>Selecione o  livro</h3>
          <h3>Livro</h3>
          <select id="livro" name="livro" onChange={onChanged}>
            <option value="">Selecione um Livro</option>
            {livros.map((livro) => {
              return (
                <option value={livro.idLivro} key={livro.idLivro}>
                  {livro.nomeLivro}
                </option>
              );
            })}
          </select>
          <button>Click me</button>
        </form>
      </div>
    </div>
  );
};

export default FormularioExemplar;
