import React from "react";
import FormularioPessoa from "./formularios/formularioPessoa";

const Pessoa = () => {
       const columnsAtrasados = [
              {
                      name: 'Nome',
                      selector: row => row.pessoa.nome,
                 
              },
              {
                      name: 'Livro',
                      selector: row => row.exemplar.livro.nomeLivro,
              
              },
              {
                      name: 'Data devolução',
                      selector: row => row.dataDevolucao,
              
              },
      
   
      ];

  return (
    <div className="sec__one">
      <h1> Cadastro de pessoas </h1>
      <div className="sec__one">
        <div className="container_midless">
          <div className="title">
            <h1 className="align_center">
              {dataEmprestados.length} Emprestados{" "}
            </h1>
          </div>
          <DataTable columns={columnsEmprestados} data={dataEmprestados} />{" "}
        </div>
      </div>
      <FormularioPessoa />
    </div>
  );
};

export default Pessoa;
