import React, { useEffect, useState } from 'react';
import { encode } from "base-64";

const formularioEmprestimo = () => {

  const [livros, setLivros] = useState([]);
  const [pessoas, setPessoas] = useState([]);

  let headers = new Headers();
  headers.append("Content-Type", "text/json");
  headers.set("Authorization", "Basic " + encode("rhyan" + ":" + "123"));

 


  return (
    <div className="container_midless">
      <div className="paddingContainer">
        <form>
          <label>Livro</label>
          <br />
          <select id="state">
            <option value="">Selecione um livro</option>
            {livros.map((livro) => {
              const { idLivro, nomeLivro } = livro;
              return <option value={idLivro}>{nomeLivro}</option>;
            })}
          </select>
          <br />
          <label>Pessoa</label>
          <br />
          {pessoas.map((pessoa) => {
            const { idPessoa, nome } = pessoa;
            return <option value={idPessoa}>{nome}</option>;
          })}
        </form>
      </div>
    </div>
  );
};

export default formularioEmprestimo;
