import React, { useEffect, useState } from 'react';
import { encode } from "base-64";

const FormularioEmprestimo = () => {
  const [formValues, setFormValues] = useState({});
 
  const onChangePessoa = (e) => {
    e.preventDefault();
    const {value, name} = e.target;
    setFormValues(e.target)
    console.log({...formValues, [name]:value});
  }

   
  const onChangeLivro = (e) => {
    e.preventDefault();
    const {value, name} = e.target;
    setFormValues(e.target)
    console.log({...formValues, [name]:value});
  }

  const[pessoas, setPessoas] = useState([]);
  const[livros, setLivros] = useState([]);
    let headers = new Headers();
    headers.append('Content-Type', 'text/json');
    headers.set('Authorization', 'Basic ' + encode("rhyan" + ":" + "123"));
    useEffect(()=>{
        fetch("http://192.168.1.6:8080/livro/buscarTodos", {
                method: 'GET', headers: headers,
        }).then((response) => response.json())
                .then(
                        (result) => {
                            setLivros(result);
                        },
                        (error) => {
                                window.alert(error);
                        }
                )
}, []);
useEffect(()=>{
  fetch("http://192.168.1.6:8080/pessoa/buscarTodas", {
          method: 'GET', headers: headers,
  }).then((response) => response.json())
          .then(
                  (result) => {
                    setPessoas(result);
                  },
                  (error) => {
                          window.alert(error);
                  }
          )
}, []);



  return (
    <div className="containerForm">
      <div className="paddingContainer">
        <form>
          <h3>Livro</h3>
          <select id="livro" name='livro' onChange={onChangeLivro}>
            <option value="">Selecione um livro</option>
             {livros.map((livro) => {
              return <option value={livro.idLivro} key={livro.idLivro}>{livro.nomeLivro}</option>;
            })}
          </select><br/>
          <h3>Pessoa</h3>
          <select id="pessoa" name='pessoa' onChange={onChangePessoa}>
            <option value="">Selecione um livro</option>
            {pessoas.map((pessoa) => {
              return <option value={pessoa.idPessoa} key={pessoa.idPessoa}>{pessoa.nome}</option>;
            })}
          </select>
         
        </form>
      </div>
    </div>
  );
};

export default FormularioEmprestimo;
