import React from 'react'
import '../index.css';
import DataTable from "react-data-table-component";
import { encode } from "base-64";

const Dashboard = () => {
        var dataAtrasados = [];
        var dataEditora = [];
        let headers = new Headers();
        headers.append('Content-Type', 'text/json');
        headers.set('Authorization', 'Basic ' + encode("rhyan" + ":" + "123"));
        fetch("http://192.168.1.6:8080/emprestimo/getAtrasados", {
                method: 'GET', headers: headers,
        }).then((response) => response.json())
                .then(
                        (result) => {
                                dataAtrasados = result;
                        },
                        (error) => {
                        }
                )

        fetch("http://192.168.1.6:8080/editora/buscarTodasEditoras", {
                method: 'GET', headers: headers,
        }).then((response) => response.json())
                .then(
                        (result) => {
                                dataAtrasados = result;
                        },
                        (error) => {
                        }
                )

        const columnsAtrasados = [
                {
                        name: 'Nome',
                        selector: row => row.dataDevolucao,
                },
                {
                        name: 'Data devolução',
                        selector: row => row.pessoa.nome,
                },
        ];


        const columnsEditora = [
                {
                        name: 'Editora',
                        selector: row => row.nome,
                },
                {
                        name: 'Data devolução',
                        selector: row => row.quantidadeLivros,
                },
        ];


        return (
                <div className="sec__one">
                        <div className='container_midless'>
                                <div className='title'><h1 className='align_center'>Atrasados</h1></div>
                                <div className='paddingContainer'> </div><DataTable
                                        columns={columnsAtrasados}
                                        data={dataAtrasados}
                                />

                        </div>
                        <div className='container_midless'>
                                <div className='title'><h1 className='align_center'>Relatorio</h1></div>
                                <div className='paddingContainer'>
                                </div><DataTable
                                        columns={columnsEditora}
                                        data={dataEditora}
                                />
                        </div>




                </div>
        )
}
export default Dashboard
