import React from "react";
import Dropdown from 'react-dropdown';
import '../../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class RpgForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "investigator3" };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
  }
  render() {
    const options = [
      'one', 'two', 'three'
    ];
    const defaultOption = options[0];

    return (
      <div className="sec__one">
        <div className="container_midless">
          <div className="paddingContainer">
            <form onSubmit={this.handleSubmit}>
             
               <h3>Livro</h3>
                <Dropdown className='dropdown' options={options} onChange={this._onSelect} value={defaultOption} placeholder="Select an option" />;
        
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default RpgForm;
