import React from "react";
import FormularioCategoria from "./formularios/formularioCategoria";

const Categoria = () => {
  const [dataPessoas, setData] = useState([]);

  let headers = new Headers();
  headers.append("Content-Type", "text/json");
  headers.set("Authorization", "Basic " + encode("rhyan" + ":" + "123"));
  useEffect(() => {
    fetch("http://192.168.1.6:8080/categoria/buscarTodas", {
      method: "GET",
      headers: headers,
    })
      .then((response) => response.json())
      .then(
        (result) => {
          setData(result);
        },
        (error) => {
          window.alert(error);
        }
      );
  }, []);
  const columnsCategoria = [
    {
      name: "Nome",
      selector: (row) => row.nome,
    },
    {
      name: "RG",
      selector: (row) => row.rg,
    },
  ];
  return (
    <div className="sec__one">
      <div className="container_midless">
        <div className="title">
          <h1 className="align_center">{dataPessoas.length} Cadastradas </h1>
        </div>
        <DataTable columns={columnsCategoria} data={dataPessoas} />{" "}
      </div>
      <FormularioCategoria />
    </div>
  );
};

export default Categoria;
