import React from "react";

class RpgForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'investigator3'};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  handleSubmit(event) {
      event.preventDefault();
    }
  
    render() {
      return (
        <div className="sec__one">
        <div className='container_midless'>
           <div className="paddingContainer">
           <form onSubmit={this.handleSubmit}>
          <label>
            Escolha seu personagem para gerar a ficha:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="investigator1">Diana</option>
            </select>
          </label>
          <input type="submit" value="Escolher!"/>
        </form>
           </div>
        </div>
        </div>
      );
    }
    
  }
  
export default RpgForm;
