import React, { useEffect, useState } from "react";
import { encode } from "base-64";

const FormularioPessoa = () => {
  const [nome, setNome] = useState('')
  const [rg, setRg] = useState('')
  const [cpf, setCpf] = useState('')
  const [cepRes, setCepRes] = useState('')
  const [numeroRes, setNumeroRes] = useState('')
  const [compleRes, setCompleRes] = useState('')
  const [cepDom, setCepCom] = useState('')
  const [numeroDom, setNumeroCom] = useState('')
  const [compleDom, setCompleCom] = useState('')

  let headers = new Headers();

  headers.set("Authorization", "Basic " + encode("rhyan" + ":" + "123"));
  
  const onSubmit = (e) => {
    headers.append("Content-Type", "application/json");
    const data = {
      "pessoa": {
        "idPessoa": formValues.pessoa
      },
      "exemplar":{
        "livro": {
          "idLivro": formValues.livro
        } 
      }
    }
    e.preventDefault();
    fetch("http://192.168.1.6:8080/emprestimo/criar", {
      method: "POST",
      headers: headers,
      body: JSON.stringify(data)
    })
      .then((response) => response.json())
      .then(
        (result) => {
          window.alert(result.message);
        },
        (error) => {
          window.alert(error);
        }
      );
  };


  return (
    <div className="containerFormLarge" >
      <div className="paddingContainer">
        <form onSubmit={onSubmit}>
          <h3>Nome</h3>
          <input onChange={event => setNome(event.target.value)} className="field" type="text" name="name"/>
          <h3>RG</h3>
          <input  onChange={event => setRg(event.target.value)}  type="number" name="name"/>
          <h3>cpf</h3>
          <input  onChange={event => setCpf(event.target.value)} type="number" name="name"/>
          <br/>
          <br/>
          <h3>CEP(Endereço Comercial)</h3>
          <input  onChange={event => setCepCom(event.target.value)} type="number" name="name"/>
          <h3>Numero</h3>
          <input onChange={event => setNumeroCom(event.target.value)}  type="number" name="name"/>
          <h3>Complemento</h3>
          <br/>
          <input onChange={event => setCompleCom(event.target.value)} type="text" name="name"/>
          <h3>CEP(Endereço Residencial)</h3>
          <input  onChange={event => setCepRes(event.target.value)} type="number" name="name"/>
          <h3>Numero</h3>
          <input  onChange={event => setNumeroRes(event.target.value)} type="number" name="name"/>
          <h3>Complemento</h3>
          <input  onChange={event => setCompleRes(event.target.value)} type="text" name="name"/>       
          <button >Click me</button>
        </form>
      </div>
    </div>
  );
};

export default FormularioPessoa;
